procedure.def:
BEGIN
return CAST ( (EXTRACT(EPOCH FROM now()) + EXTRACT(timezone FROM timestamptz 'epoch')) as INTEGER);
END;

BEGIN
return  (CASE WHEN time_in IS NULL THEN NULL ELSE MIN(CAST (
        (EXTRACT(EPOCH FROM time_in) + EXTRACT(timezone FROM timestamptz 'epoch'))
     as BIGINT),2147483647)  END);
END;
